tokenToBuy = tokenA


# 4)If conditions are met we buy the token
def buy():
    print('we start the snipe')
   

    nonce = web3.eth.get_transaction_count(sender_address)

    #Buy token with bnb use 'swapExactETHForTokens' or if you want to buy with busd or token use 
    # swapExactTokenForTokensSupportingFeeOnTransferTokens for rebase token or use swapExactETHForTokens
    pancakeswap2_txn = contractbuy.functions.swapExactETHForTokens(
    #tokenA, tokenB when buying with 2 token   
    0, #tokenB, 
    [spend,tokenToBuy],
    sender_address,
    (int(time.time()) + 10000)
    ).buildTransaction({
    
    'from': sender_address,
    'value': web3.toWei(0.01,'ether'),#This is the Token(BNB) amount you want to Swap from  | Comment out for token
    'gas':210000,
    'gasPrice': web3.toWei('5','gwei'),
    'nonce': nonce,
    })
    #Sign transaction with priavte key here
    signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key='ENTER YOUR PRIVATEKEY')
    tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
    print("Snipe was succesfull, bought: " + web3.toHex(tx_token))
    exit()


#3)Check liquidity balance

def checkLp():
    print('cehck for balance')
    lp = pair
    lpcontract = web3.eth.contract(address=lp, abi=lpabi)
    balance = lpcontract.functions.getReserves().call()
    b = balance[0]
    print(b)
    if b > 0:
        print('balance detected')
        buy()
    else:
        print('no balance detected')
        test()
    

#2) check if pair has been created

def address():
    print('check for address')
    global pair
    pair = contract.functions.getPair(tokenA, tokenB).call()
    print(pair)
    if pair != web3.toChecksumAddress('0x0000000000000000000000000000000000000000'):
        print('lp address detected')
        time.sleep(1)
        checkLp()
        


